function checkAvailable() {
    for(var i=0;i<arrProduct.length;i++)
    {
        var data = {
            'id': arrProduct[i].id
        };
        $.ajax({
            type: 'POST',
            url: '/checkavailable',
            data: data,
            async: false
        }).done(function (data) {
                arrProduct[i].name = data.ten;
                arrProduct[i].price = parseInt(data.gia)*parseInt(arrProduct[i].count);
                arrProduct[i].rootprice =data.gia;
        }).fail(function (data) {
        });
    }
};
